mapper.select:
SELECT FM_ITEM_NO, FM_ITEM_NAME, FM_ITEM_SELLPRICE, FM_ITEM_COUNT, FM_CATEGORY_NO
FROM FM_ITEM
WHERE FM_CATEGORY_NO = #{cNo}

SELECT FM_ITEM_NO, FM_ITEM_NAME, FM_ITEM_SELLPRICE, FM_ITEM_COUNT, FM_CATEGORY_NO
FROM FM_ITEM
WHERE FM_ITEM_NO = #{iNo}

mapper.update:
UPDATE FM_ITEM

	FM_ITEM_NAME = #{iName}
	FM_ITEM_SELLPRICE = #{iSellprice}
	FM_ITEM_COUNT = #{iCount}

WHERE FM_ITEM_NO = #{iNo}

mapper.select:
SELECT FM_ITEM_IMG_NO, FM_ITEM_IMG_NAME, FM_ITEM_STORED_IMG_NAME
FROM FM_ITEM_IMG
WHERE FM_ITEM_IMG_NO = #{parentSeq}

SELECT FM_ITEM_IMG_NO, FM_ITEM_IMG_NAME, FM_ITEM_STORED_IMG_NAME
FROM FM_ITEM_IMG
WHERE FM_ITEM_IMG_NO = #{iNo}
