mapper.select:
SELECT COUNT(*) AS "rCount"
FROM FM_REVIEW
WHERE FM_ITEM_NO = ${value}

SELECT FM_REVIEW_NO, FM_REVIEW_IMG_NAME, FM_REVIEW_STORED_NAME
FROM FM_REVIEW_IMG
WHERE FM_REVIEW_NO = #{rNo}

SELECT FM_REVIEW_IMG_NO, FM_REVIEW_NO, FM_REVIEW_IMG_NAME, FM_REVIEW_STORED_NAME
FROM FM_REVIEW_IMG
WHERE FM_REVIEW_NO = #{rNo}

mapper.update:
		UPDATE FM_REVIEW
		

			FM_REVIEW_TITLE = #{rTitle},
			FM_REVIEW_CONTENT = #{rContent}
		
		WHERE FM_REVIEW_NO = #{rNo}

mapper.select:
SELECT count(FM_USER_NO)
FROM FM_REVIEW
WHERE FM_USER_NO = #{uNo}
AND FM_ORDER_NO = #{oNo}
