mapper.update:
UPDATE FM_USER
FM_USER_NICKNAME = #{nickName}, FM_USER_PASSWORD = #{password}, FM_USER_SALT = #{salt},
WHERE FM_USER_NO = #{uNo}

mapper.select:
SELECT COUNT(*)
FROM FM_USER
WHERE FM_USER_EMAIL = #{email}

mapper.update:
UPDATE FM_USER
SET
FM_USER_POINT = FM_USER_POINT + #{pHistory}
WHERE FM_USER_NO = #{uNo}

mapper.select:
SELECT rp.*
FROM(
    SELECT ROWNUM rnum, p.*
    FROM(
            SELECT FM_POINT_NO, FM_POINT_HISTORY, FM_POINT_CRE_DATE
            FROM FM_POINT 
            WHERE FM_USER_NO = #{uNo}
            
            	AND FM_ORDER_NO = #{oNo}
            
            ORDER BY FM_POINT_NO DESC	) p
    ) rp
    WHERE rp.rnum BETWEEN #{start} AND #{end}
